import { NavLink } from "react-router-dom";
import { loadScript, publicUrlFor } from "../../../globals/constants";
import { useEffect } from "react";

export default function PrivacyPolicyPage() {

    useEffect(() => {
        loadScript('/assets/js/bg-image-move.js', true);
    }, []);

    return (
        <>
            <div className="privacy-policy-page bg-cover overlay-wraper bg-image-moving2" style={{ backgroundImage: `url(${publicUrlFor('/assets/images/main-slider/slider3/bg-large3.jpg')})` }}>
                <div className="overlay-main bg-sky-blue opacity-07" />
                <div className="container">
                    <div className="privacy-policy-content">
                        <h1 className="title-style-2">Privacy Policy</h1>
                        <p>Welcome to Source Huts. We are committed to protecting your privacy and ensuring the security of your personal information. This Privacy Policy outlines how we collect, use, and safeguard your data.</p>

                        <h3>1. Information We Collect</h3>
                        <p>We collect personal information such as your name, email address, shipping details, and payment information when you place an order or sign up on our website.</p>

                        <h3>2. How We Use Your Information</h3>
                        <p>Your data is used to process orders, improve our services, and provide personalized shopping experiences. We may also send promotional offers if you opt-in for marketing communications.</p>

                        <h3>3. Data Protection</h3>
                        <p>We implement robust security measures to protect your personal information against unauthorized access, alteration, or disclosure.</p>

                        <h3>4. Sharing Information</h3>
                        <p>We do not sell, trade, or rent your personal information to third parties. We may share data with trusted partners for order fulfillment and delivery purposes.</p>

                        <h3>5. Your Rights</h3>
                        <p>You have the right to access, update, or delete your personal information. Contact us if you wish to exercise these rights.</p>

                        <h3>6. Updates to This Policy</h3>
                        <p>We may update this Privacy Policy periodically. Changes will be posted on this page with the updated effective date.</p>

                        <p>If you have any questions about our Privacy Policy, please contact us at <strong>support@sourcehuts.com</strong>.</p>

                        <NavLink to="/index" className="btn-half site-button">
                            <span>Back To Home</span><em />
                        </NavLink>
                    </div>
                </div>
            </div>
        </>
    );
}
